mapper.update:
UPDATE t_user
C_USERNAME=#{username}, C_IMAGE_URL=#{imageUrl}, C_CITY=#{city}, C_COUNTRY=#{country}, C_PROVINCE=#{province}, C_GENDER=#{gender},
WHERE C_ID = #{id};

UPDATE t_user
   SET C_USERNAME = #{username}, C_IMAGE_URL = #{imageUrl}, C_CITY = #{city}, C_COUNTRY = #{country},
       C_PROVINCE = #{province}, C_GENDER = #{gender}
   WHERE C_ID = #{id};
